import React, {useState} from 'react';
import Row from 'react-bootstrap/Row';
import Hero from './Hero';
import PubFilter from './PubFilter';

import {useHistory} from "react-router-dom";



const Portada = (props) =>{

  const [searchPub, setSearchPub] = useState (null);

  const [filtros, setFiltros] = useState ({
    categoria : '',

});

    const history = useHistory();

    const [terminoBuscado, setTerminoBuscado] = useState('');

    const handleTerminoBuscadoChange = (event)=>{

        history.push("/");

        let busqueda = event.target.value;
        setTerminoBuscado(busqueda);

        props.onSearchPubs(busqueda)
    }

    const handleSearchPubs = (terminoBuscado)=>{
    
      if(terminoBuscado === ''){
        terminoBuscado = null;
      }
  
  
      setSearchPub(terminoBuscado)
  
    }
  
    const handleFilterChange = filtros =>{
      setFiltros(filtros);
      
    }

    return(

            <Row>
          <Hero     value={props.terminoBuscado}
                    onSearchPubs={handleSearchPubs} 
                    filtros={filtros}
                    onFilterChange={handleFilterChange}/>{/* filtros */}
                    
                    <PubFilter onFilterChange={handleFilterChange}/>
            </Row>
    );
}

export default Portada;
