import React from 'react';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import '../components/styles/TarjetaImagen.css';
import iconoNofovotiro from '../like.png';
import {Link} from 'react-router-dom';

const TarjetaImagen = (props)=>{
    return(

        <Col md={4} lg={3} xs={12} xl={3} className=" mb-3 d-flex align-items-stretch justif-content-center">
                 
            <Card className="cardImg"> 
                <Card.Body className="cardBodyImg">

                    <Link to={"/fotos/" + props.id} className="nav-link p-0">
                        <img src={props.foto} className="card-img-top" alt={props.titulo}></img>
                    </Link>
                </Card.Body>
                
                <Card.Footer className="my-0">

                        {props.type === 'productos' &&
                        <a className="nav-link text-right p-0"  href="#">
                            <img src={iconoNofovotiro} alt="icono para dar like"></img>
                        </a>

                        }

                        <small className="text-muted">{props.titulo}</small>
                        <small className="text-muted"> <a className="nav-link p-0"  href="#">{props.usuario}</a></small>
                </Card.Footer>
            
            </Card>
        </Col>


    );
}

export default TarjetaImagen;
