import React, {useState} from 'react';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import '../components/styles/Hero.css';

import {useHistory} from "react-router-dom";

const Buscar = (props)=>{

    const history = useHistory();

    const [terminoBuscado, setTerminoBuscado] = useState('');

    const handleTerminoBuscadoChange = (event)=>{

        history.push("/");

        let busqueda = event.target.value;
        setTerminoBuscado(busqueda);

        props.onSearchPubs(busqueda)
    }

    return(
        <div className="Hero">
            <h1 className="heroh1">Descubrí las mejores ímagenes</h1>
            
        <Row className="justify-content-center" style={{width:"100%"}}>
            <Col xs={10} md={8} lg={6}>
        <InputGroup className="inputNavHero">
    
        <FormControl className="inputNavHero1" variant="" 
                    type= "text "
                    value={terminoBuscado}
                    onChange={handleTerminoBuscadoChange}
                    />

            <InputGroup.Append className="inputNavHero2">
                <DropdownButton as={InputGroup} title="Buscar" id="bg-nested-dropdown" variant="">
                    <Dropdown.Item>Fotos</Dropdown.Item>
                    <Dropdown.Item>Galerías</Dropdown.Item>
                    <Dropdown.Item>Fotógrafos</Dropdown.Item>
                </DropdownButton>
            </InputGroup.Append>
        </InputGroup>
        </Col>
        </Row>
        <p className="heroP">Contactá a los mejores fotógrafos, descargá y subí fotos.</p>
        </div>


    )
}

export default Buscar;
