import React, {useState, useEffect} from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const PubFilter = (props) =>{

    const [categorias, setCategorias] = useState([{id:'', nombre:'Todas'}]);

    const categoriaRef = useState('null');  // esta vinculada al select.
                                            // La uso como referencia al control para que cuando cambie el filtro traiga el cambio
                

    useEffect(()=>{
        fetch('http://localhost:8888/categorias')
        .then( response => response.json()
        ).then(
            dataCategorias =>{
                setCategorias(dataCategorias);
            }
        )
    }, [])

    const categoriasOptions = ()=>{
        let categories = categorias.map( categoria =>{
            return (
                <option value={categoria.id}>
                    {categoria.nombre}
                </option>
            )
        })

        categories.unshift(<option value=''>Todas</option>); //esta linea agrega al desplegable de categorias Todas

        return categories;
    }

    const handleFilterChange = () =>{
        console.log("se activo el filtro")
        
    }

    return(
            <Row className=" my-3 justify-content-center m-0 bg-light p-3">
                  
                  <Col xs={12} md={11} lg={8}>
                        <Form>
                            <Row>

                                <Col xs={12} md={5}>
                                    <Form.Group>
                                        <Form.Label>Categorias</Form.Label>
                                        <Form.Control   as="select"
                                                        onChange={handleFilterChange()}>
                                                        {categoriasOptions()}

                                        </Form.Control>
                                    </Form.Group>
                                </Col>   

                                <Col xs={7} md={4}>
                                    <Form.Label>Precio</Form.Label>
                                    <Form.Row>
                                        <Col xs={5}>
                                            <Form.Control   type="number"
                                                            placeholder="desde"
                                                            />
                                        </Col>

                                        <Col xs={5}>
                                            <Form.Control   type="number"
                                                            placeholder="hasta"
                                                            />
                                        </Col>

                                        <Col xs={2}>
                                            <Button variant="secondary"> </Button>
                                        </Col>

                                    </Form.Row>
                                </Col>

                                <Col xs={5} md={3}>
                                    <Form.Group>
                                        <Form.Label>Orden</Form.Label>
                                        <Form.Control   as="select">
                                            <option value="menor_precio">Menor precio</option>
                                            <option value="mayor_precio">Mayor precio</option>
                                        </Form.Control>                                        
                                    </Form.Group>
                                </Col>

                            </Row>
                        </Form>
                  </Col>  
            </Row>

    );

}

export default PubFilter;
