import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const FotosEditorModals = (props) =>{

//Decalaración de las variables de estado
const [fotoTitulo, setFotoTitulo] =useState('asdasdasdS');
const [fotoPrecio, setFotoPrecio] = useState('');
const [fotoImage, setFotoImage] = useState('');


return(

    <Modal show={props.show} onHide={props.handleHide}>
        <Modal.Header closeButton>
            <Modal.Title>Publicación</Modal.Title>
        </Modal.Header>

        <Modal.Body>
            <Form>
                <Form.Group>
                    <Form.Label>Titulo</Form.Label>

                    <Form.Control 
                                type="text"
                                value={fotoTitulo}
                                />
                </Form.Group>

                <Form.Group>
                    <Form.Label>precio</Form.Label>

                    <Form.Control type="text"></Form.Control>
                </Form.Group>


                <Form.Group>
                    <Form.Label>imagen</Form.Label>

                    <Form.Control type="file"></Form.Control>
                </Form.Group>

            </Form>
        </Modal.Body>

        <Modal.Footer>
            <Button variant="secondary">
                Cancelar
            </Button>

            <Button variant="success">
                Subir
            </Button>

        </Modal.Footer>

    </Modal>
);
}

export default FotosEditorModals
