import React, {useState, useEffect} from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import {useParams} from 'react-router-dom';
import {fontAwesomeIcon, FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faEdit, faTrash, faCloudDownloadAlt, faMugHot} from '@fortawesome/free-solid-svg-icons';


const FotosDetails = ()=>{

    let {id} = useParams(); //devuelve el id para usar en el fetch

    let [foto, setFoto] = useState([]);

    useEffect(

        ()=>{
            fetch('http://localhost:8888/fotos/' + id).then(
                response => response.json()
            ).then(data =>{
                setFoto(data);
                console.log(data)
                }
            )
        }, [] //corta el ciclo de actualización que entra en un bucle.
    )
    //
    return(
        <Row className="ml-2 mr-2">
            <Col md={9}>
                <img src={foto.foto} className="img-fluid" style={{width:"100%"}}/>
            </Col>

            <Col md={3}>
                <h3 >{foto.titulo}</h3>
                <h4>{foto.id}</h4>

                <Button className="m-2"><FontAwesomeIcon icon={faMugHot} style={{marginRight:"10px"}} />Café</Button>
                <Button size="lg" block m-2  variant="success"><FontAwesomeIcon icon={faCloudDownloadAlt} style={{marginRight:"20px"}} />Descargar</Button>

            
            </Col>
        </Row>

    );
}

export default FotosDetails;
