import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const LoginModals = (props)=>{

    //funcion que maneja el envio del inicio de sesión
    const handleLoginClick = ()=>{

        //defino la dirección del servidor
        let url = 'http://localhost:8888/auth';

        //los recibe los parametros y van a ser pasados a altributo bidy dek fetch
        let params = {
            user: 'pepe',
            password: '123456'
        };

        //fetch hasta la url
        //recibe como parametros el metodo por el cual se va a hacer el login
        fetch(url, {
                method: 'POST',
                credentials: 'include',
                body: JSON.stringify( params),
                headers: {
                            'content-type' : 'aplication/json'
                         }
                    }
            ).then( response => response.json()
            ).then( data => {
                console.log(data);
            });
        }


    return(
        <Modal show={props.show} onHide={props.handleHide}>
            <Modal.Header closeButton>
                <Modal.Title>Iniciar Sesión</Modal.Title>
            </Modal.Header>

            <Modal.Body>
                <Form.Group>
                    <Form.Label>Nombre de Usuario</Form.Label>
                    <Form.Control type="text"/>
                </Form.Group>

                <Form.Group>
                    <Form.Label>Contraseña</Form.Label>
                    <Form.Control type="password"/>
                </Form.Group>
            </Modal.Body>

            <Modal.Footer>
                <Button variant="secondary"
                //recibe el valor desde navigationBar
                onClick={props.handleHide}>Cancelar</Button>

                <Button variant="primary"
                        //onClcik recibe la funcion que se encarga de manejar el aceptar.
                        onClick={handleLoginClick}
                        >Ingresar</Button>
            </Modal.Footer>

        </Modal>
    );
}

export default LoginModals;

