import React from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import '../components/styles/TarjetaImagen.css';
import iconoNoFovotiro from '../like.png';
import iconoFovotiro from '../likeRed.png';
import {Link} from 'react-router-dom';
import {fontAwesomeIcon, FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faEdit, faTrash} from '@fortawesome/free-solid-svg-icons';

const TarjetaImagen = (props)=>{

    const handleEditClick = ()=>{
        //recibe el id de la publicación 
        //onEditClick viene como props de ListadoFotos
        props.onEditClick(props.id);
    }

 //función que recibe el click para borrar la publicación    
const handleDeliteClick =()=>{
//recibe el id de la publicación
//hace el llamado a onDeleteClick de ListadoFotos(padre)
props.onDeleteClick(props.id); 
}


const handleFavClick =()=>{
    props.onChangeFavStatus(props.isFav, props.id, props.user.id);
}
//RETERNO DE TarjetaImagen
    return(

        <Col md={4} lg={3} xs={12} xl={4} className=" p-1 d-flex align-items-stretch justif-content-center">
                 
            <Card className="cardImg"> 
                <Card.Body className="cardBodyImg">
                    
                    <Link to={"/fotos/" + props.id} className="nav-link p-0">
                        <img src={props.foto} className="card-img-top" alt={props.titulo}></img>
                    </Link>
                    

                </Card.Body>
                
                <Card.Footer className="my-0">
<Row>


                <Col className="text-left p-0 n-0">
                        <small className="text-muted">{props.titulo}</small>
                        <small className="text-muted"> <a className="nav-link p-0"  href="#">{props.usuario}</a></small>
                </Col>

                <Col md={2}className="text-right p-0 m-0">
                    <Row>
                        {(props.type === 'fotos'|| props.type ==='favoritos') && props.user &&
                            <img style={{cursor: 'pointer'}} 
                            src={props.isFav ? iconoFovotiro : iconoNoFovotiro } alt="icono para dar like"
                                onClick={handleFavClick}>
                            </img>
                        }
                    </Row>
                </Col>
                

                </Row>
                </Card.Footer>

                {props.type === 'mispublicaciones' &&
            
                <Row className="my-2">
 
                    <Col>
                    <Button variant="light"
                            onClick={handleEditClick}
                            >
                        <FontAwesomeIcon icon={faEdit} />
                    </Button>
                    </Col> 
                                      
                    <Col>
                    <Button variant="light"
                            onClick={handleDeliteClick}
                            >
                        <FontAwesomeIcon icon={faTrash} />
                    </Button>
                    </Col>

                </Row>
                

                }
            
            </Card>
        </Col>


    );
}

export default TarjetaImagen;
